import{_ as a}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as l,d as e}from"./app.6a6b0b18.js";const d="/assets/icon/\u6808\u6307\u4EE4\u96C6.png",o="/assets/icon/\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6.png",t="/assets/icon/jvm\u67B6\u6784.png",r={},c=e('<h1 id="jvm-\u8FD0\u884C\u65F6\u6570\u636E\u533A" tabindex="-1"><a class="header-anchor" href="#jvm-\u8FD0\u884C\u65F6\u6570\u636E\u533A" aria-hidden="true">#</a> JVM-\u8FD0\u884C\u65F6\u6570\u636E\u533A</h1><p>\u672C\u6587\u5185\u5BB9:</p><ol><li>JVM\u4ECB\u7ECD&amp;JVM\u7EC4\u6210\u90E8\u5206</li><li>\u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784</li><li>\u4E09\u79CD\u865A\u62DF\u673A\u4ECB\u7ECD</li><li><strong>\u8FD0\u884C\u65F6\u6570\u636E\u533A(\u91CD\u70B9)</strong></li></ol><h2 id="jvm\u4ECB\u7ECD-jvm\u7EC4\u6210\u90E8\u5206" tabindex="-1"><a class="header-anchor" href="#jvm\u4ECB\u7ECD-jvm\u7EC4\u6210\u90E8\u5206" aria-hidden="true">#</a> JVM\u4ECB\u7ECD&amp;JVM\u7EC4\u6210\u90E8\u5206</h2><h3 id="java\u7A0B\u5E8F\u7684\u6267\u884C\u8FC7\u7A0B" tabindex="-1"><a class="header-anchor" href="#java\u7A0B\u5E8F\u7684\u6267\u884C\u8FC7\u7A0B" aria-hidden="true">#</a> Java\u7A0B\u5E8F\u7684\u6267\u884C\u8FC7\u7A0B</h3><p>\u4E00\u4E2A Java \u7A0B\u5E8F\uFF0C\u9996\u5148\u7ECF\u8FC7 <code>javac</code> \u7F16\u8BD1\u6210 <code>.class</code> \u6587\u4EF6\uFF0C\u7136\u540E <code>JVM</code> \u5C06\u5176\u52A0\u8F7D\u5230<code>\u65B9\u6CD5\u533A</code>\uFF0C\u6267\u884C \u5F15\u64CE\u5C06\u4F1A\u6267\u884C\u8FD9\u4E9B\u5B57\u8282\u7801\u3002 \u6267\u884C\u65F6\uFF0C\u4F1A\u7FFB\u8BD1\u6210\u64CD\u4F5C\u7CFB\u7EDF\u76F8\u5173\u7684\u51FD\u6570\u3002<code>JVM</code> \u4F5C\u4E3A <code>.class</code> \u6587\u4EF6\u7684\u7FFB\u8BD1 \u5B58\u5728\uFF0C\u8F93\u5165\u5B57\u8282\u7801\uFF0C\u8C03\u7528\u64CD\u4F5C\u7CFB\u7EDF\u51FD\u6570\u3002</p><h3 id="\u4EC0\u4E48\u662Fjvm" tabindex="-1"><a class="header-anchor" href="#\u4EC0\u4E48\u662Fjvm" aria-hidden="true">#</a> \u4EC0\u4E48\u662FJVM</h3><ul><li><p><strong>JVM\u5C31\u662F\u4E00\u79CD\u89C4\u8303</strong>\uFF0C\u5BF9\u4E8E\u6C47\u7F16\u7684\u8BED\u8A00\u89C4\u8303\u548C\u5904\u7406\uFF0C\u800C\u4E0A\u5C42\u7684\u9AD8\u7EA7\u8BED\u8A00\u5B9E\u9645\u4E0A\u6211\u4EEC\u5C31\u7406\u89E3\u4E3A\u662F\u4E00\u5806\u7684\u5B57\u7B26\u4E32</p></li><li><p>JVM \u5168\u79F0 Java Virtual Machine\uFF0C\u4E5F\u5C31\u662F Java \u865A\u62DF\u673A\u3002\u5B83\u80FD\u8BC6\u522B .class\u540E\u7F00 \u7684\u6587\u4EF6\uFF0C\u5E76\u4E14\u80FD\u591F\u89E3\u6790\u5B83\u7684\u6307\u4EE4\uFF0C\u6700\u7EC8\u8C03\u7528\u64CD\u4F5C\u7CFB\u7EDF\u4E0A\u7684\u51FD\u6570\uFF0C\u5B8C\u6210\u6211\u4EEC\u60F3\u8981\u7684\u64CD\u4F5C</p></li></ul><div class="custom-container info"><p class="custom-container-title">Java\u7A0B\u5E8F\u7684\u6267\u884C\u8FC7\u7A0B</p><p>Java \u6587\u4EF6-&gt;\u7F16\u8BD1\u5668&gt;\u5B57\u8282\u7801-&gt;JVM-&gt;\u673A\u5668\u7801</p></div><h3 id="jvm\u4E0E\u5B57\u8282\u7801\u6587\u4EF6" tabindex="-1"><a class="header-anchor" href="#jvm\u4E0E\u5B57\u8282\u7801\u6587\u4EF6" aria-hidden="true">#</a> JVM\u4E0E\u5B57\u8282\u7801\u6587\u4EF6</h3><ul><li><p>JAVA\u5B57\u8282\u7801\uFF0C\u6307\u7684\u662FJAVA\u8BED\u8A00\u7F16\u8BD1(\u901A\u8FC7javac\u7F16\u8BD1.java\u540E\u7F00\u6587\u4EF6)\u6210\u7684\u5B57\u8282\u7801\uFF0C\u51C6\u786E\u7684\u8BF4\u4EFB\u4F55\u80FD\u5728JVM\u5E73\u53F0\u4E0A\u6267\u884C\u7684\u5B57\u8282\u7801\u683C\u5F0F\u90FD\u662F\u4E00\u6837\u7684\uFF0C\u6240\u4EE5\u5E94\u8BE5\u7EDF\u79F0\u4E3A<code>JVM\u5B57\u8282\u7801</code></p></li><li><p>\u4E0D\u540C\u7684\u7F16\u8BD1\u5668\uFF0C\u53EF\u4EE5\u7F16\u8BD1\u51FA\u76F8\u540C\u7684\u5B57\u8282\u7801\u6587\u4EF6\uFF0C\u5B57\u8282\u7801\u6587\u4EF6\u4E5F\u53EF\u4EE5\u5728\u4E0D\u540C\u7684JVM\u4E0A\u8FD0\u884C\u3002</p></li><li><p>JAVA\u865A\u62DF\u673A\u4E0EJAVA\u8BED\u8A00\u5E76\u6CA1\u6709\u76F4\u63A5\u8054\u7CFB\uFF0C\u4ED6\u53EA\u662F\u7279\u5B9A\u7684\u4E8C\u8FDB\u5236\u6587\u4EF6\u683C\u5F0F.class\u6587\u4EF6\u6709\u6240\u5173\u8054\uFF0C CLASS\u6587\u4EF6\u4E2D\u5305\u542BJVM\u865A\u62DF\u673A\u6307\u4EE4\u96C6(bytecodes)\u548C\u7B26\u53F7\u8868\uFF0C\u8FD8\u6709\u4E00\u4E9B\u5176\u4ED6\u8F85\u52A9\u4FE1\u606F\u3002</p></li></ul><h3 id="jvm\u7EC4\u6210\u90E8\u5206" tabindex="-1"><a class="header-anchor" href="#jvm\u7EC4\u6210\u90E8\u5206" aria-hidden="true">#</a> JVM\u7EC4\u6210\u90E8\u5206</h3><p>JVM\u7531<code>\u7C7B\u52A0\u8F7D\u5668</code>\uFF0C<code>\u8FD0\u884C\u65F6\u6570\u636E\u533A</code>\uFF0C<code>\u6267\u884C\u5F15\u64CE</code>\u4E09\u4E2A\u90E8\u5206\u7EC4\u6210\u3002</p><div class="custom-container note"><p class="custom-container-title">JVM\u7EC4\u6210\u90E8\u5206</p><ul><li>\u7C7B\u52A0\u8F7D\u5668\uFF1A\u5C06\u7F16\u8BD1\u597D\u7684class\u6587\u4EF6\u52A0\u8F7D\u5230JVM\u8FDB\u7A0B\u4E2D</li><li>\u8FD0\u884C\u65F6\u6570\u636E\u533A\uFF1A\u5B58\u653E\u7CFB\u7EDF\u6267\u884C\u8FC7\u7A0B\u4E2D\u4EA7\u751F\u7684\u6570\u636E</li><li>\u6267\u884C\u5F15\u64CE\uFF1A\u7528\u6765\u6267\u884C\u6C47\u7F16\u53CA\u5F53\u524D\u8FDB\u7A0B\u5185\u6240\u8981\u5B8C\u6210\u7684\u4E00\u4E9B\u5177\u4F53\u5185\u5BB9</li></ul></div><h2 id="\u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#\u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784" aria-hidden="true">#</a> \u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784</h2><p>JAVA\u7F16\u8BD1\u5668\u6307\u4EE4\u662F\u57FA\u4E8E\u6808\u7684\u6307\u4EE4\u96C6\u67B6\u6784\uFF0C\u800C\u53E6\u4E00\u79CD\u6307\u4EE4\u662F\u57FA\u4E8E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784\u3002</p><h3 id="\u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784-1" tabindex="-1"><a class="header-anchor" href="#\u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784-1" aria-hidden="true">#</a> \u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784</h3><ul><li>\u8BBE\u8BA1\u4E0E\u5B9E\u73B0\u7B80\u5355\uFF0C\u9002\u7528\u4E8E\u8D44\u6E90\u53D7\u9650\u7684\u7CFB\u7EDF</li><li>\u907F\u5F00\u5BC4\u5B58\u5668\u7684\u5206\u914D\u95EE\u9898\uFF0C\u4F7F\u75280\u5730\u5740\u6307\u4EE4\u65B9\u5F0F</li><li>\u6307\u4EE4\u6D41\u4E2D\u7684\u6307\u4EE4\u64CD\u4F5C\u8FC7\u7A0B\u57FA\u4E8E\u6808\uFF0C\u4E14\u4F4D\u6570\u5C0F(8\u4F4D)\uFF0C\u7F16\u8BD1\u5668\u5BB9\u6613\u5B9E\u73B0</li><li>\u4E0D\u9700\u8981\u786C\u4EF6\u652F\u6301\uFF0C\u53EF\u79FB\u690D\u6027\u9AD8</li></ul><img src="'+d+'" style="height:250px;width:200px;" alt=""><h3 id="\u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784-2" tabindex="-1"><a class="header-anchor" href="#\u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784-2" aria-hidden="true">#</a> \u6808\u6307\u4EE4\u96C6\u67B6\u6784\u4E0E\u5BC4\u5B58\u5668\u6307\u4EE4\u96C6\u67B6\u6784</h3><ul><li>x86\u4E8C\u8FDB\u5236\u6307\u4EE4\u96C6(16\u4F4D), android\u7684davlik\u865A\u62DF\u673A\u4F7F\u7528\u7684\u662F\u8FD9\u79CD\u67B6\u6784</li><li>\u4F9D\u8D56\u4E8E\u786C\u4EF6\uFF0C\u53EF\u79FB\u690D\u6027\u5DEE</li><li>\u6027\u80FD\u4F18\u79C0\u6267\u884C\u9AD8\u6548</li><li>\u66F4\u5C11\u65F6\u95F4\u6267\u884C\u4E00\u4E2A\u64CD\u4F5C</li><li>\u57FA\u4E8E\u5BC4\u5B58\u5668\u67B6\u6784\u7684\u6307\u4EE4\u4E00\u822C\u4EE51-3\u4E2A\u5730\u5740\u6307\u4EE4\u4E3A\u4E3B</li></ul><img src="'+o+'" style="height:100px;width:200px;" alt=""><h2 id="\u4E09\u79CD\u865A\u62DF\u673A\u4ECB\u7ECD" tabindex="-1"><a class="header-anchor" href="#\u4E09\u79CD\u865A\u62DF\u673A\u4ECB\u7ECD" aria-hidden="true">#</a> \u4E09\u79CD\u865A\u62DF\u673A\u4ECB\u7ECD</h2><h3 id="hotspot\u865A\u62DF\u673A" tabindex="-1"><a class="header-anchor" href="#hotspot\u865A\u62DF\u673A" aria-hidden="true">#</a> Hotspot\u865A\u62DF\u673A</h3><ul><li>Hotspot\u865A\u62DF\u673A\u96B6\u5C5E\u4E8Esun</li><li>JDK1.3\u5F00\u53D1Hotspot\u6210\u4E3A\u9ED8\u8BA4\u865A\u62DF\u673A</li><li>\u4E00\u822C\u9762\u8BD5\u6240\u6709\u63D0\u5230\u7684JVM\u865A\u62DF\u673A\u90FD\u9ED8\u8BA4\u6307\u4EE3\u7684\u662FHotspot\u865A\u62DF\u673A</li></ul><h3 id="dalvik\u865A\u62DF\u673A-art\u865A\u62DF\u673A" tabindex="-1"><a class="header-anchor" href="#dalvik\u865A\u62DF\u673A-art\u865A\u62DF\u673A" aria-hidden="true">#</a> Dalvik\u865A\u62DF\u673A&amp;ART\u865A\u62DF\u673A</h3><ul><li>Dalvik\u865A\u62DF\u673A\u96B6\u5C5E\u4E8Egoogle</li><li>Dalvik\u662F\u4E00\u6B3E\u4E0D\u662FJVM\u7684JVM\u865A\u62DF\u673A\u3002\u672C\u8D28\u4E0A\u4ED6\u6CA1\u6709\u9075\u5FAA\u4E0EJVM\u89C4\u8303,\u4E0D\u80FD\u76F4\u63A5\u8FD0\u884Cjava Class\u6587\u4EF6</li><li>\u4ED6\u7684\u7ED3\u6784\u57FA\u4E8E\u5BC4\u5B58\u5668\u7ED3\u6784\uFF0C\u800C\u4E0D\u662FJVM\u6808\u67B6\u6784</li><li>\u6267\u884C\u7684\u662F\u7F16\u8BD1\u540E\u7684Dex\u6587\u4EF6\uFF0C\u6267\u884C\u6548\u7387\u8F83\u9AD8, \u4E8EAndroid5.0\u540E\u88ABART\u66FF\u6362</li></ul><h2 id="\u8FD0\u884C\u65F6\u6570\u636E\u533A" tabindex="-1"><a class="header-anchor" href="#\u8FD0\u884C\u65F6\u6570\u636E\u533A" aria-hidden="true">#</a> \u8FD0\u884C\u65F6\u6570\u636E\u533A</h2><p>JVM\u6574\u4F53\u67B6\u6784\uFF1A</p><img src="'+t+'" alt=""><div class="custom-container note"><p class="custom-container-title">\u8FD0\u884C\u65F6\u6570\u636E\u533A</p><ul><li>\u7EBF\u7A0B\u72EC\u4EAB\uFF1A\u865A\u62DF\u673A\u6808\uFF0C\u672C\u5730\u65B9\u6CD5\u6808\uFF0C\u7A0B\u5E8F\u8BA1\u6570\u5668</li><li>\u7EBF\u7A0B\u5171\u4EAB\uFF1A\u65B9\u6CD5\u533A\uFF0C\u5806\u533A</li></ul></div><h3 id="\u865A\u62DF\u673A\u6808" tabindex="-1"><a class="header-anchor" href="#\u865A\u62DF\u673A\u6808" aria-hidden="true">#</a> \u865A\u62DF\u673A\u6808</h3><ul><li>\u865A\u62DF\u673A\u6808\u6570\u636E\u7ED3\u6784\uFF1A \u5148\u8FDB\u540E\u51FA(FILO)\u7684\u6570\u636E\u7ED3\u6784\uFF0C</li><li>\u865A\u62DF\u673A\u6808\u7684\u4F5C\u7528: \u5728JVM\u8FD0\u884C\u8FC7\u7A0B\u4E2D\u5B58\u50A8\u5F53\u524D\u7EBF\u7A0B\u8FD0\u884C\u65B9\u6CD5\u6240\u9700\u7684\u6570\u636E\uFF0C\u6307\u4EE4\u3001\u8FD4\u56DE\u5730\u5740\u3002</li><li>\u865A\u62DF\u673A\u6808\u662F\u57FA\u4E8E\u7EBF\u7A0B\u7684: \u54EA\u6015\u4F60\u53EA\u6709\u4E00\u4E2A main() \u65B9\u6CD5\uFF0C\u4E5F\u662F\u4EE5\u7EBF\u7A0B\u7684\u65B9\u5F0F\u8FD0\u884C\u7684\u3002\u5728\u7EBF\u7A0B\u7684\u751F\u547D\u5468\u671F\u4E2D\uFF0C\u53C2\u4E0E\u8BA1\u7B97\u7684\u6570\u636E\u4F1A\u9891\u7E41\u5730\u5165\u6808\u548C\u51FA\u6808\uFF0C\u6808\u7684\u751F\u547D\u5468\u671F\u662F\u548C\u7EBF\u7A0B\u4E00\u6837\u7684\u3002</li><li>\u865A\u62DF\u673A\u6808\u7684\u5927\u5C0F\u7F3A\u7701\u4E3A1M\uFF0C\u53EF\u7528\u53C2\u6570 \u2013Xss\u8C03\u6574\u5927\u5C0F\uFF0C\u4F8B\u5982-Xss256k\u3002</li><li>\u6808\u5E27\uFF1A\u5728\u6BCF\u4E2A Java \u65B9\u6CD5\u88AB\u8C03\u7528\u7684\u65F6\u5019\uFF0C\u90FD\u4F1A\u521B\u5EFA\u4E00\u4E2A\u6808\u5E27\uFF0C\u5E76\u5165\u6808\u3002\u4E00\u65E6\u65B9\u6CD5\u5B8C\u6210\u76F8\u5E94\u7684\u8C03\u7528\uFF0C\u5219\u51FA\u6808\u3002</li><li>\u6808\u5E27\u5927\u4F53\u90FD\u5305\u542B\u56DB\u4E2A\u533A\u57DF\uFF1A(\u5C40\u90E8\u53D8\u91CF\u8868\u3001\u64CD\u4F5C\u6570\u6808\u3001\u52A8\u6001\u8FDE\u63A5\u3001\u8FD4\u56DE\u5730\u5740)</li></ul><p>\u540C\u65F6\uFF0C\u865A\u62DF\u673A\u6808\u8FD9\u4E2A\u5185\u5B58\u4E5F\u4E0D\u662F\u65E0\u9650\u5927\uFF0C\u5B83\u6709\u5927\u5C0F\u9650\u5236\uFF0C\u9ED8\u8BA4\u60C5\u51B5\u4E0B\u662F1M\u3002 \u5982\u679C\u6211\u4EEC\u4E0D\u65AD\u7684\u5F80\u865A\u62DF\u673A\u6808\u4E2D\u5165\u6808\u5E27\uFF0C\u4F46\u662F\u5C31\u662F\u4E0D\u51FA\u6808\u7684\u8BDD\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u865A\u62DF\u673A\u6808\u5C31\u4F1A\u6EA2\u51FA\u3002</p><h3 id="\u6808\u5E27" tabindex="-1"><a class="header-anchor" href="#\u6808\u5E27" aria-hidden="true">#</a> \u6808\u5E27</h3><ol><li>\u5C40\u90E8\u53D8\u91CF\u8868</li></ol><p>\u987E\u540D\u601D\u4E49\u5C31\u662F\u5C40\u90E8\u53D8\u91CF\u7684\u8868\uFF0C\u7528\u4E8E\u5B58\u653E\u6211\u4EEC\u7684\u5C40\u90E8\u53D8\u91CF\u7684\uFF08\u65B9\u6CD5\u4E2D\u7684\u53D8\u91CF\uFF09\u3002\u9996\u5148\u5B83\u662F\u4E00\u4E2A32\u4F4D\u7684\u957F\u5EA6\uFF0C\u4E3B\u8981\u5B58\u653E\u6211\u4EEC\u7684Java\u7684\u516B\u5927\u57FA\u7840\u6570\u636E\u7C7B\u578B\uFF0C\u4E00\u822C32\u4F4D\u5C31\u53EF\u4EE5\u5B58\u653E\u4E0B\uFF0C\u5982\u679C\u662F64\u4F4D\u7684\u5C31\u4F7F\u7528\u9AD8\u4F4E\u4F4D\u5360\u7528\u4E24\u4E2A\u4E5F\u53EF\u4EE5\u5B58\u653E\u4E0B\uFF0C\u5982\u679C\u662F\u5C40\u90E8\u7684\u4E00\u4E9B\u5BF9\u8C61\uFF0C\u6BD4\u5982\u6211\u4EEC\u7684Object\u5BF9\u8C61\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u5B58\u653E\u5B83\u7684\u4E00\u4E2A\u5F15\u7528\u5730\u5740\u5373\u53EF\u3002</p><p>1.1 \u5C40\u90E8\u53D8\u91CF\u8868slot(\u53D8\u91CF\u69FD)</p><p>slot\u662F\u5C40\u90E8\u53D8\u91CF\u8868\u7684\u57FA\u7840\u5355\u4F4D \u5728\u8868\u4E2D\uFF0C32\u4F4D\u7C7B\u578B\u6570\u636E\u5360\u7528\u4E00\u4E2Aslot\uFF0C 64\u4F4D\u6570\u636E\u5360\u75282\u4F4D</p><p>1.2 slot\u590D\u7528\u95EE\u9898</p><p>\u5C40\u90E8\u53D8\u91CF\u8868\u4E2Dslot\u662F\u53EF\u4EE5\u670D\u7528\u7684\uFF0C\u5982\u679C\u4E00\u4E2A\u5C40\u90E8\u53D8\u91CF\u8FC7\u4E86\u5176\u4ED6\u4F5C\u7528\u57DF\uFF0C\u90A3\u4E48\u5176\u4F5C\u7528\u57DF\u4E4B\u540E\u58F0\u660E\u7684\u65B0\u7684\u5C40\u90E8\u53D8\u91CF\u6709\u53EF\u80FD\u4F1A\u590D\u7528\u8FD9\u4E2Asolt\uFF0C\u4EE5\u4FBF\u8282\u7701\u8D44\u6E90\u3002</p><ol start="2"><li>\u64CD\u4F5C\u6570\u6808</li></ol><p>\u5B58\u653Ejava\u65B9\u6CD5\u6267\u884C\u7684\u64CD\u4F5C\u6570\u7684\uFF0C\u5B83\u5C31\u662F\u4E00\u4E2A\u6808\uFF0C\u5148\u8FDB\u540E\u51FA\u7684\u6808\u7ED3\u6784\uFF0C\u64CD\u4F5C\u6570\u6808\uFF0C\u5C31\u662F\u7528\u6765\u64CD\u4F5C\u7684\uFF0C\u64CD\u4F5C\u7684\u7684\u5143\u7D20\u53EF\u4EE5\u662F\u4EFB\u610F\u7684java\u6570\u636E\u7C7B\u578B\uFF0C\u6240\u4EE5\u6211\u4EEC\u77E5\u9053\u4E00\u4E2A\u65B9\u6CD5\u521A\u521A\u5F00\u59CB\u7684\u65F6\u5019\uFF0C\u8FD9\u4E2A\u65B9\u6CD5\u7684\u64CD\u4F5C\u6570\u6808\u5C31\u662F\u7A7A\u7684\u3002 \u64CD\u4F5C\u6570\u6808\u672C\u8D28\u4E0A\u662FJVM\u6267\u884C\u5F15\u64CE\u7684\u4E00\u4E2A\u5DE5\u4F5C\u533A\uFF0C\u4E5F\u5C31\u662F\u65B9\u6CD5\u5728\u6267\u884C\uFF0C\u624D\u4F1A\u5BF9\u64CD\u4F5C\u6570\u6808\u8FDB\u884C\u64CD\u4F5C\uFF0C\u5982\u679C\u4EE3\u7801\u4E0D\u4E0D\u6267\u884C\uFF0C\u64CD\u4F5C\u6570\u6808\u5176\u5B9E\u5C31\u662F\u7A7A\u7684\u3002</p><ol start="3"><li>\u8FD4\u56DE\u5730\u5740</li></ol><p>\u6B63\u5E38\u8FD4\u56DE\uFF08\u8C03\u7528<code>\u7A0B\u5E8F\u8BA1\u6570\u5668\u4E2D\u7684\u5730\u5740</code>\u4F5C\u4E3A\u8FD4\u56DE\uFF09\u3001\u5F02\u5E38\u7684\u8BDD\uFF08\u901A\u8FC7<code>\u5F02\u5E38\u5904\u7406\u5668\u8868</code>&lt;\u975E\u6808\u5E27\u4E2D&gt;)</p><p>\u65E0\u8BBA\u901A\u8FC7\u90A3\u79CD\u65B9\u5F0F\u9000\u51FA\uFF0C\u5728\u65B9\u6CD5\u9000\u51FA\u540E\u8FD4\u56DE\u5230\u8BE5\u65B9\u6CD5\u88AB\u8C03\u7528\u7684\u4F4D\u7F6E.</p><ol start="4"><li>\u52A8\u6001\u8FDE\u63A5</li></ol><ul><li>\u6BCF\u4E00\u4E2A\u6808\u5E27\u5185\u90E8\u90FD\u5305\u542B\u4E00\u4E2A\u6267\u884C\u8FD0\u884C\u65F6\u5E38\u91CF\u6C60\u4E2D\u8BE5\u6808\u5E27\u6240\u8FF0\u65B9\u6CD5\u7684\u5F15\u7528\u3002\u5305\u542B\u8FD9\u4E2A\u5F15\u7528\u7684\u76EE\u7684\u662F\u4E3A \u4E86\u652F\u6301\u5F53\u524D\u65B9\u6CD5\u7684\u4EE3\u7801\u80FD\u591F\u5B9E\u73B0\u52A8\u6001\u94FE\u63A5(invokeDynamic\u6307\u4EE4)</li><li>\u5728Java\u6E90\u6587\u4EF6\u88AB\u7F16\u8BD1\u5230\u5B57\u8282\u7801\u6587\u4EF6\u4E2D\u662F\uFF0C\u6240\u6709\u7684\u53D8\u91CF\u548C\u65B9\u6CD5\u5F15\u7528\u90FD\u4F5C\u4E3A\u7B26\u53F7\u5F15\u7528\u4FDD\u5B58\u5728class\u6587\u4EF6\u7684 <code>\u5E38\u91CF\u6C60</code>\u91CC\u3002</li><li>\u4F8B\u5982\uFF1A\u63CF\u8FF0\u4E00\u4E2A\u65B9\u6CD5\u8C03\u7528\u53E6\u5916\u4E00\u4E2A\u65B9\u6CD5\u662F\uFF0C\u5C31\u662F\u901A\u8FC7\u5E38\u91CF\u6C60\u4E2D\u7684\u6267\u884C\u65B9\u6CD5\u7B26\u53F7\u5F15\u7528\u6765\u6807\u8BC6\uFF0C\u90A3\u4E48\u52A8\u6001 \u94FE\u63A5\u7684\u4F5C\u7528\u5C31\u662F<strong>\u4E3A\u4E86\u5C06\u8FD9\u4E9B\u7B26\u53F7\u5F15\u7528\u8F6C\u6362\u4E3A\u8C03\u7528\u65B9\u6CD5\u7684\u76F4\u63A5\u5F15\u7528</strong></li></ul><h3 id="\u7A0B\u5E8F\u8BA1\u6570\u5668" tabindex="-1"><a class="header-anchor" href="#\u7A0B\u5E8F\u8BA1\u6570\u5668" aria-hidden="true">#</a> \u7A0B\u5E8F\u8BA1\u6570\u5668</h3><ul><li><p>\u8F83\u5C0F\u7684\u5185\u5B58\u7A7A\u95F4\uFF0C\u5F53\u524D\u7EBF\u7A0B\u6267\u884C\u7684\u5B57\u8282\u7801\u7684\u884C\u53F7\u6307\u793A\u5668\uFF1B\u5404\u7EBF\u7A0B\u4E4B\u95F4\u72EC\u7ACB\u5B58\u50A8\uFF0C\u4E92\u4E0D\u5F71\u54CD\u3002\u7A0B\u5E8F\u8BA1\u6570\u5668\u662F\u4E00\u5757\u5F88\u5C0F\u7684\u5185\u5B58\u7A7A\u95F4\uFF0C\u4E3B\u8981\u7528\u6765\u8BB0\u5F55\u5404\u4E2A\u7EBF\u7A0B\u6267\u884C\u7684\u5B57\u8282\u7801\u7684\u5730\u5740\uFF0C\u4F8B\u5982\uFF0C\u5206\u652F\u3001\u5FAA\u73AF\u3001\u8DF3\u8F6C\u3001\u5F02\u5E38\u3001\u7EBF\u7A0B\u6062\u590D\u7B49\u90FD\u4F9D\u8D56\u4E8E\u8BA1\u6570\u5668\u3002</p></li><li><p>\u7531\u4E8E Java \u662F\u591A\u7EBF\u7A0B\u8BED\u8A00\uFF0C\u5F53\u6267\u884C\u7684\u7EBF\u7A0B\u6570\u91CF\u8D85\u8FC7 CPU \u6838\u6570\u65F6\uFF0C\u7EBF\u7A0B\u4E4B\u95F4\u4F1A\u6839\u636E\u65F6\u95F4\u7247\u8F6E\u8BE2\u4E89\u593A CPU \u8D44\u6E90\u3002\u5982\u679C\u4E00\u4E2A\u7EBF\u7A0B\u7684\u65F6\u95F4\u7247\u7528\u5B8C\u4E86\uFF0C\u6216\u8005\u662F\u5176\u5B83\u539F\u56E0\u5BFC\u81F4\u8FD9\u4E2A\u7EBF\u7A0B\u7684 CPU \u8D44\u6E90\u88AB\u63D0\u524D\u62A2\u593A\uFF0C\u90A3\u4E48\u8FD9\u4E2A\u9000\u51FA\u7684\u7EBF\u7A0B\u5C31\u9700\u8981\u5355\u72EC\u7684\u4E00\u4E2A\u7A0B\u5E8F\u8BA1\u6570\u5668\uFF0C\u6765\u8BB0\u5F55\u4E0B\u4E00\u6761\u8FD0\u884C\u7684\u6307\u4EE4.</p></li><li><p>\u56E0\u4E3AJVM\u662F\u865A\u62DF\u673A\uFF0C\u5185\u90E8\u6709\u5B8C\u6574\u7684\u6307\u4EE4\u4E0E\u6267\u884C\u7684\u4E00\u5957\u6D41\u7A0B\uFF0C\u6240\u4EE5\u5728\u8FD0\u884CJava\u65B9\u6CD5\u7684\u65F6\u5019\u9700\u8981\u4F7F\u7528\u7A0B\u5E8F\u8BA1\u6570\u5668\uFF08\u8BB0\u5F55\u5B57\u8282\u7801\u6267\u884C\u7684\u5730\u5740\u6216\u884C\u53F7\uFF09\uFF0C\u5982\u679C\u662F\u9047\u5230\u672C\u5730\u65B9\u6CD5\uFF08native\u65B9\u6CD5\uFF09\uFF0C\u8FD9\u4E2A\u65B9\u6CD5\u4E0D\u662FJVM\u6765\u5177\u4F53\u6267\u884C\uFF0C\u6240\u4EE5\u7A0B\u5E8F\u8BA1\u6570\u5668\u4E0D\u9700\u8981\u8BB0\u5F55\u4E86\uFF0C\u8FD9\u4E2A\u662F\u56E0\u4E3A\u5728\u64CD\u4F5C\u7CFB\u7EDF\u5C42\u9762\u4E5F\u6709\u4E00\u4E2A\u7A0B\u5E8F\u8BA1\u6570\u5668\uFF0C\u8FD9\u4E2A\u4F1A\u8BB0\u5F55\u672C\u5730\u4EE3\u7801\u7684\u6267\u884C\u7684\u5730\u5740.</p></li><li><p>\u53E6\u5916\u7A0B\u5E8F\u8BA1\u6570\u5668\u4E5F\u662FJVM\u4E2D\u552F\u4E00\u4E0D\u4F1AOOM(OutOfMemory)\u7684\u5185\u5B58\u533A\u57DF\u3002</p></li></ul><h3 id="\u672C\u5730\u65B9\u6CD5\u6808" tabindex="-1"><a class="header-anchor" href="#\u672C\u5730\u65B9\u6CD5\u6808" aria-hidden="true">#</a> \u672C\u5730\u65B9\u6CD5\u6808</h3><ul><li><p>\u672C\u5730\u65B9\u6CD5\u6808\u8DDF Java \u865A\u62DF\u673A\u6808\u7684\u529F\u80FD\u7C7B\u4F3C\uFF0CJava \u865A\u62DF\u673A\u6808\u7528\u4E8E\u7BA1\u7406 Java \u51FD\u6570\u7684\u8C03\u7528\uFF0C\u800C\u672C\u5730\u65B9\u6CD5\u6808\u5219\u7528\u4E8E\u7BA1\u7406\u672C\u5730\u65B9\u6CD5\u7684\u8C03\u7528\u3002\u4F46\u672C\u5730\u65B9\u6CD5\u5E76\u4E0D\u662F\u7528 Java \u5B9E\u73B0\u7684\uFF0C\u800C\u662F\u7531 C \u8BED\u8A00\u5B9E\u73B0\u7684(\u6BD4\u5982Object.hashcode\u65B9\u6CD5)\u3002</p></li><li><p>\u672C\u5730\u65B9\u6CD5\u6808\u662F\u548C\u865A\u62DF\u673A\u6808\u975E\u5E38\u76F8\u4F3C\u7684\u4E00\u4E2A\u533A\u57DF\uFF0C\u5B83\u670D\u52A1\u7684\u5BF9\u8C61\u662F native \u65B9\u6CD5\u3002\u4F60\u751A\u81F3\u53EF\u4EE5\u8BA4\u4E3A\u865A\u62DF\u673A\u6808\u548C\u672C\u5730\u65B9\u6CD5\u6808\u662F\u540C\u4E00\u4E2A\u533A\u57DF\u3002 \u865A\u62DF\u673A\u89C4\u8303\u65E0\u5F3A\u5236\u89C4\u5B9A\uFF0C\u5404\u7248\u672C\u865A\u62DF\u673A\u81EA\u7531\u5B9E\u73B0 \uFF0CHotSpot\u76F4\u63A5\u628A\u672C\u5730\u65B9\u6CD5\u6808\u548C\u865A\u62DF\u673A\u6808\u5408\u4E8C\u4E3A\u4E00 \u3002</p></li></ul><h3 id="\u65B9\u6CD5\u533A" tabindex="-1"><a class="header-anchor" href="#\u65B9\u6CD5\u533A" aria-hidden="true">#</a> \u65B9\u6CD5\u533A</h3><p>\u65B9\u6CD5\u533A\uFF08Method Area\uFF09\u662F\u53EF\u4F9B\u5404\u6761\u7EBF\u7A0B\u5171\u4EAB\u7684\u8FD0\u884C\u65F6\u5185\u5B58\u533A\u57DF\u3002\u5B83\u7528\u4E8E<code>\u5B58\u50A8\u8FD0\u884C\u65F6\u5E38\u91CF\u6C60</code>\u3001\u5DF2\u88AB\u865A\u62DF\u673A\u52A0\u8F7D\u7684<code>\u7C7B\u578B\u4FE1\u606F</code>\u3001<code>\u5E38\u91CF</code>\u3001<code>\u9759\u6001\u53D8\u91CF</code>\u3001\u53CA\u65F6\u7F16\u8BD1\u5668\u7F16\u8BD1\u540E\u7684\u4EE3\u7801\u7F13\u5B58\u7B49 \u65B9\u6CD5\u533A\u662FJVM\u5BF9\u5185\u5B58\u7684\u201C\u903B\u8F91\u5212\u5206\u201D\uFF0C\u5728JDK1.7\u53CA\u4E4B\u524D\u5F88\u591A\u5F00\u53D1\u8005\u90FD\u4E60\u60EF\u5C06\u65B9\u6CD5\u533A\u79F0\u4E3A\u201C\u6C38\u4E45\u4EE3\u201D\uFF0C\u662F\u56E0\u4E3A\u5728HotSpot \u865A\u62DF\u673A\u4E2D\uFF0C\u8BBE\u8BA1\u4EBA\u5458\u4F7F\u7528\u4E86\u6C38\u4E45\u4EE3\u6765\u5B9E\u73B0\u4E86 JVM \u89C4\u8303\u7684\u65B9\u6CD5\u533A\u3002\u5728JDK1.8\u53CA\u4EE5\u540E\u4F7F\u7528\u4E86<code>\u5143\u7A7A\u95F4</code>\u6765\u5B9E\u73B0\u65B9\u6CD5\u533A\u3002</p><ul><li><strong>\u8FD0\u884C\u65F6\u5E38\u91CF\u6C60</strong>: \u8FD0\u884C\u65F6\u5E38\u91CF\u6C60\uFF08Runtime Constant Pool\uFF09\u662F\u6BCF\u4E00\u4E2A\u7C7B\u6216\u63A5\u53E3\u7684\u5E38\u91CF\u6C60\uFF08Constant_Pool\uFF09\u7684\u8FD0\u884C\u65F6\u8868\u793A\u5F62\u5F0F\uFF0C\u5B83\u5305\u62EC\u4E86\u82E5\u5E72\u79CD\u4E0D\u540C\u7684\u5E38\u91CF\uFF1A\u4ECE\u7F16\u8BD1\u671F\u53EF\u77E5\u7684\u6570\u503C\u5B57\u9762\u91CF\u5230\u5FC5\u987B\u8FD0\u884C\u671F\u89E3\u6790\u540E\u624D\u80FD\u83B7\u5F97\u7684\u65B9\u6CD5\u6216\u5B57\u6BB5\u5F15\u7528\u3002</li><li>\u7C7B\u578B\u4FE1\u606F\uFF1A \u5305\u542B\u7C7Bclass\\\u63A5\u53E3interface\\\u679A\u4E3Eenum\\\u6CE8\u89E3annotation</li><li>\u65B9\u6CD5\u4FE1\u606F\uFF1A \u6267\u884C\u5B57\u8282\u7801\uFF0C\u672C\u5730\u53D8\u91CF\u8868\uFF0C\u64CD\u4F5C\u6570\u6808\uFF0C\u52A8\u6001\u94FE\u63A5\uFF0C\u65B9\u6CD5\u51FA\u53E3\uFF0C\u5F02\u5E38\u8868</li></ul><h3 id="\u5806\u533A" tabindex="-1"><a class="header-anchor" href="#\u5806\u533A" aria-hidden="true">#</a> \u5806\u533A</h3><ul><li><p>\u5806\u662F JVM \u4E0A\u6700\u5927\u7684\u5185\u5B58\u533A\u57DF\uFF0C\u6211\u4EEC\u7533\u8BF7\u7684\u51E0\u4E4E\u6240\u6709\u7684\u5BF9\u8C61\uFF0C\u90FD\u662F\u5728\u8FD9\u91CC\u5B58\u50A8\u7684\u3002</p></li><li><p>\u5806\u7A7A\u95F4\u4E00\u822C\u662F\u7A0B\u5E8F\u542F\u52A8\u65F6\uFF0C\u5C31\u7533\u8BF7\u4E86\uFF0C\u4F46\u662F\u5E76\u4E0D\u4E00\u5B9A\u4F1A\u5168\u90E8\u4F7F\u7528\u3002\u5806\u4E00\u822C\u8BBE\u7F6E\u6210\u53EF\u4F38\u7F29\u7684.</p></li><li><p>\u968F\u7740\u5BF9\u8C61\u7684\u9891\u7E41\u521B\u5EFA\uFF0C\u5806\u7A7A\u95F4\u5360\u7528\u7684\u8D8A\u6765\u8D8A\u591A\uFF0C\u5C31\u9700\u8981\u4E0D\u5B9A\u671F\u7684\u5BF9\u4E0D\u518D\u4F7F\u7528\u7684\u5BF9\u8C61\u8FDB\u884C\u56DE\u6536\u3002\u8FD9\u4E2A\u5728 Java \u4E2D\uFF0C\u5C31\u53EB\u4F5C GC\uFF08Garbage Collection\uFF09\u3002</p></li><li><p>\u90A3\u4E00\u4E2A\u5BF9\u8C61\u521B\u5EFA\u7684\u65F6\u5019\uFF0C\u5230\u5E95\u662F\u5728\u5806\u4E0A\u5206\u914D\uFF0C\u8FD8\u662F\u5728\u6808\u4E0A\u5206\u914D\u5462\uFF1F\u8FD9\u548C\u4E24\u4E2A\u65B9\u9762\u6709\u5173\uFF1A\u5BF9\u8C61\u7684\u7C7B\u578B\u548C\u5728 Java \u7C7B\u4E2D\u5B58\u5728\u7684\u4F4D\u7F6E\u3002 Java \u7684\u5BF9\u8C61\u53EF\u4EE5\u5206\u4E3A\u57FA\u672C\u6570\u636E\u7C7B\u578B\u548C\u666E\u901A\u5BF9\u8C61\u3002</p></li><li><p>\u5BF9\u4E8E\u666E\u901A\u5BF9\u8C61\u6765\u8BF4\uFF0CJVM \u4F1A\u9996\u5148\u5728\u5806\u4E0A\u521B\u5EFA\u5BF9\u8C61\uFF0C\u7136\u540E\u5728\u5176\u4ED6\u5730\u65B9\u4F7F\u7528\u7684\u5176\u5B9E\u662F\u5B83\u7684\u5F15\u7528\u3002\u6BD4\u5982\uFF0C\u628A\u8FD9\u4E2A\u5F15\u7528\u4FDD\u5B58\u5728\u865A\u62DF\u673A\u6808\u7684\u5C40\u90E8\u53D8\u91CF\u8868\u4E2D\u3002</p></li><li><p>\u5BF9\u4E8E\u57FA\u672C\u6570\u636E\u7C7B\u578B\u6765\u8BF4\uFF08byte\u3001short\u3001int\u3001long\u3001float\u3001double\u3001char)\uFF0C\u6709\u4E24\u79CD\u60C5\u51B5\u3002 <strong>\u5F53\u5728\u65B9\u6CD5\u4F53\u5185\u58F0\u660E\u4E86\u57FA\u672C\u6570\u636E\u7C7B\u578B\u7684\u5BF9\u8C61\uFF0C\u5B83\u5C31\u4F1A\u5728\u6808\u4E0A\u76F4\u63A5\u5206\u914D\u3002\u5176\u4ED6\u60C5\u51B5\uFF0C\u90FD\u662F\u5728\u5806\u4E0A\u5206\u914D\u3002</strong></p></li></ul><h3 id="\u76F4\u63A5\u5185\u5B58-\u5806\u5916\u5185\u5B58" tabindex="-1"><a class="header-anchor" href="#\u76F4\u63A5\u5185\u5B58-\u5806\u5916\u5185\u5B58" aria-hidden="true">#</a> \u76F4\u63A5\u5185\u5B58(\u5806\u5916\u5185\u5B58)</h3><ul><li>\u76F4\u63A5\u5185\u5B58\u4E0D\u662F\u865A\u62DF\u673A\u8FD0\u884C\u65F6\u6570\u636E\u533A\u7684\u4E00\u90E8\u5206\uFF0C\u4E5F\u4E0D\u662FJAVA \u865A\u62DF\u673A\u89C4\u8303\u4E2D\u5B9A\u4E49\u7684\u5185\u5B58\u533A\u57DF\uFF0C\u5982\u679C\u4F7F\u7528\u4E86NIO,\u8FD9\u5757\u533A\u57DF\u4F1A\u88AB\u9891\u7E41\u4F7F\u7528\uFF0C\u5728java\u5806\u5185\u53EF\u4EE5\u7528dictiByteBuffer\u5BF9\u8C61 \u76F4\u63A5\u5F15\u7528\u64CD\u4F5C\uFF0C</li><li>\u8FD9\u5757\u5185\u5B58\u4E0D\u53D7java\u5806\u5927\u5C0F\u9650\u5236\uFF0C\u4F46\u53D7\u672C\u673A\u603B\u5185\u5B58\u7684\u9650\u5236\uFF0C\u53EF\u4EE5\u901A\u8FC7-XX:MaxDirectMemorySize\u6765\u8BBE\u7F6E\uFF08\u9ED8\u8BA4\u4E0E\u5806\u5185\u5B58\u6700\u5927\u503C\u4E00\u6837\uFF09\uFF0C\u6240\u4EE5\u4E5F\u4F1A\u51FA\u73B0OOM\u5F02\u5E38\u3002</li><li>\u5176\u4ED6\u5806\u5916\u5185\u5B58\uFF0C\u4E3B\u8981\u662F\u6307\u4F7F\u7528\u4E86Unsafe \u6216\u8005\u5176\u4ED6 JNI \u624B\u6BB5\u76F4\u63A5\u76F4\u63A5\u7533\u8BF7\u7684\u5185\u5B58\u3002</li><li>\u5806\u5916\u5185\u5B58\u7684\u6CC4\u6F0F\u662F\u975E\u5E38\u4E25\u91CD\u7684\uFF0C\u5B83\u7684\u6392\u67E5\u96BE\u5EA6\u9AD8\u3001\u5F71\u54CD\u5927\uFF0C\u751A\u81F3\u4F1A\u9020\u6210\u4E3B\u673A\u7684\u6B7B\u4EA1</li></ul><h3 id="\u5185\u5B58\u6EA2\u51FA\u95EE\u9898" tabindex="-1"><a class="header-anchor" href="#\u5185\u5B58\u6EA2\u51FA\u95EE\u9898" aria-hidden="true">#</a> \u5185\u5B58\u6EA2\u51FA\u95EE\u9898</h3><p>\u5185\u5B58\u6EA2\u51FA\u6982\u5FF5\uFF1A\u65E0\u6CD5\u5206\u914D\u5185\u5B58\uFF0C\u53EF\u7528\u5185\u5B58\u4E0D\u8DB3\uFF0C\u65E0\u6CD5\u5B8C\u6210\u5185\u5B58\u5206\u914D \u5185\u5B58\u6EA2\u51FA\u573A\u666F\uFF1A</p><ul><li>\u6808\u6EA2\u51FA</li><li>\u5806\u6EA2\u51FA</li><li>\u65B9\u6CD5\u533A\u6EA2\u51FA</li><li>\u672C\u673A\u76F4\u63A5\u5185\u5B58\u6EA2\u51FA</li></ul><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><ul><li><p>JVM\u8FD0\u884C\u5185\u5B58\u7684\u6574\u4F53\u6D41\u7A0B\uFF1A</p><p>JVM\u5728\u64CD\u4F5C\u7CFB\u7EDF\u4E0A\u542F\u52A8\uFF0C\u7533\u8BF7\u5185\u5B58\uFF0C\u5148\u8FDB\u884C<code>\u8FD0\u884C\u65F6\u6570\u636E\u533A\u7684\u521D\u59CB\u5316</code>\uFF0C\u7136\u540E<code>\u628A\u7C7B\u52A0\u8F7D\u5230\u65B9\u6CD5\u533A\u4E2D</code> \u65B9\u6CD5\u7684\u6267\u884C\u548C\u9000\u51FA\u8FC7\u7A0B\u5728\u5185\u5B58\u4E0A\u7684\u4F53\u73B0\u4E0A\u5C31\u662F<code>\u865A\u62DF\u673A\u6808\u4E2D\u6808\u5E27\u7684\u5165\u6808\u548C\u51FA\u6808</code>\u3002 \u540C\u65F6\u5728\u65B9\u6CD5\u7684\u6267\u884C\u8FC7\u7A0B\u4E2D<code>\u521B\u5EFA\u7684\u5BF9\u8C61\u4E00\u822C\u60C5\u51B5\u4E0B\u90FD\u662F\u653E\u5728\u5806\u4E2D</code>\uFF0C\u6700\u540E\u5806\u4E2D\u7684\u5BF9\u8C61\u4E5F\u662F\u9700\u8981\u8FDB\u884C<code>\u5783\u573E\u56DE\u6536\u6E05\u7406</code>\u7684</p></li></ul>',64),h=[c];function s(n,p){return i(),l("div",null,h)}const v=a(r,[["render",s],["__file","\u8FD0\u884C\u65F6\u6570\u636E\u533A.html.vue"]]);export{v as default};
